import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [layer, setLayer] = useState('MQTT');
  const [folderPath, setFolderPath] = useState('');
  const [data, setData] = useState('');
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post('http://localhost:5000/run-layer', {
      layer,
      folderPath,
      data
    });
    setResult(res.data);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>RFDC Automation Runner</h2>
      <form onSubmit={handleSubmit}>
        <label>Layer:</label>
        <select value={layer} onChange={e => setLayer(e.target.value)}>
          <option value="MQTT">MQTT</option>
          <option value="Kafka">Kafka</option>
          <option value="REST">REST</option>
          <option value="DB">DB</option>
          <option value="UI">UI</option>
          <option value="Mobile">Mobile</option>
          <option value="Firmware">Firmware</option>
        </select>
        <br />
        <label>Folder Path:</label>
        <input value={folderPath} onChange={e => setFolderPath(e.target.value)} />
        <br />
        <label>Important Data:</label>
        <input value={data} onChange={e => setData(e.target.value)} />
        <br />
        <button type="submit">Run Automation</button>
      </form>
      {result && (
        <div style={{ marginTop: 20 }}>
          <h4>Result:</h4>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
