import React, { useState } from 'react';
import axios from 'axios';
import ReportViewer from './ReportViewer';

const layers = [
  { value: 'MQTT', label: 'MQTT' },
  { value: 'Kafka', label: 'Kafka' },
  { value: 'REST', label: 'REST APIs' },
  { value: 'DB', label: 'Database' },
  { value: 'UI', label: 'UI (Playwright)' },
  { value: 'Mobile', label: 'Mobile (Appium)' },
  { value: 'Firmware', label: 'Firmware' }
];

function App() {
  const [layer, setLayer] = useState('MQTT');
  const [testName, setTestName] = useState('');
  const [folderPath, setFolderPath] = useState('');
  const [data, setData] = useState('');
  const [dataFilePath, setDataFilePath] = useState('');
  const [result, setResult] = useState(null);
  const [reportUrl, setReportUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post('http://localhost:5000/run-layer', {
      layer,
      testName,
      folderPath,
      data,
      dataFilePath
    });
    setResult(res.data);
    if (res.data.reportUrl) setReportUrl(res.data.reportUrl);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e0e7ff 0%, #f8fafc 100%)',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'flex-start',
      fontFamily: 'Inter, sans-serif'
    }}>
      <div style={{
        marginTop: 40,
        background: '#fff',
        borderRadius: 16,
        boxShadow: '0 4px 24px rgba(0,0,0,0.08)',
        padding: 32,
        width: 420,
        maxWidth: '90vw'
      }}>
        <h2 style={{ color: '#3b82f6', marginBottom: 24 }}>RFDC Automation Runner</h2>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: 18 }}>
            <label style={{ fontWeight: 500 }}>Layer:</label>
            <select
              value={layer}
              onChange={e => setLayer(e.target.value)}
              style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1' }}
            >
              {layers.map(l => (
                <option key={l.value} value={l.value}>{l.label}</option>
              ))}
            </select>
          </div>
            <div style={{ marginBottom: 18 }}>
              <label style={{ fontWeight: 500 }}>Test Name:</label>
              <input
                value={testName}
                onChange={e => setTestName(e.target.value)}
                placeholder={'e.g. MQTT Publish Test'}
                style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1', width: 220 }}
              />
            </div>
          <div style={{ marginBottom: 18 }}>
            <label style={{ fontWeight: 500 }}>Test Name:</label>
            <input
              value={testName}
              onChange={e => setTestName(e.target.value)}
              placeholder={'e.g. MQTT Publish Test'}
              style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1', width: 220 }}
            />
          </div>
          <div style={{ marginBottom: 18 }}>
            <label style={{ fontWeight: 500 }}>Test Data File Path:</label>
            <input
              value={dataFilePath}
              onChange={e => setDataFilePath(e.target.value)}
              placeholder={'e.g. /tests/data/mqtt.json'}
              style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1', width: 220 }}
            />
          </div>
          <div style={{ marginBottom: 18 }}>
            <label style={{ fontWeight: 500 }}>Folder Path:</label>
            <input
              value={folderPath}
              onChange={e => setFolderPath(e.target.value)}
              placeholder={'e.g. /tests/mqtt_test.robot'}
              style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1', width: 220 }}
            />
          </div>
          <div style={{ marginBottom: 18 }}>
            <label style={{ fontWeight: 500 }}>Important Data (optional):</label>
            <input
              value={data}
              onChange={e => setData(e.target.value)}
              placeholder={'e.g. {"host":"localhost"}'}
              style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1', width: 220 }}
            />
          </div>
          <div style={{ marginBottom: 18 }}>
            <label style={{ fontWeight: 500 }}>Test Data File Path:</label>
            <input
              value={dataFilePath}
              onChange={e => setDataFilePath(e.target.value)}
              placeholder={'e.g. /tests/data/mqtt.json'}
              style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1', width: 220 }}
            />
          </div>
          <div style={{ marginBottom: 18 }}>
            <label style={{ fontWeight: 500 }}>Important Data (optional):</label>
            <input
              value={data}
              onChange={e => setData(e.target.value)}
              placeholder={'e.g. {"host":"localhost"}'}
              style={{ marginLeft: 12, padding: 6, borderRadius: 6, border: '1px solid #cbd5e1', width: 220 }}
            />
          </div>
          <button
            type="submit"
            style={{
              background: '#3b82f6',
              color: '#fff',
              border: 'none',
              borderRadius: 8,
              padding: '10px 24px',
              fontWeight: 600,
              fontSize: 16,
              cursor: 'pointer',
              boxShadow: '0 2px 8px rgba(59,130,246,0.12)'
            }}
          >Run Automation</button>
        </form>
        {result && (
          <div style={{ marginTop: 28 }}>
            <h4 style={{ color: '#64748b' }}>Result:</h4>
            <pre style={{ background: '#f1f5f9', padding: 16, borderRadius: 8 }}>{JSON.stringify(result, null, 2)}</pre>
          </div>
        )}
        <ReportViewer reportUrl={reportUrl} />
      </div>
      <footer style={{ marginTop: 40, color: '#94a3b8', fontSize: 14 }}>
        Powered by Robot Framework, BrowserStack, Allure
      </footer>
    </div>
  );
}

export default App;
