import React, { useState } from 'react';
import axios from 'axios';
import ReportViewer from './ReportViewer';

const layers = [
  { value: 'MQTT', label: 'MQTT' },
  { value: 'Kafka', label: 'Kafka' },
  { value: 'REST', label: 'REST APIs' },
  { value: 'DB', label: 'Database' },
  { value: 'UI', label: 'UI (Playwright)' },
  { value: 'Mobile', label: 'Mobile (Appium)' },
  { value: 'Firmware', label: 'Firmware' }
];

function App() {
  const [layer, setLayer] = useState('MQTT');
  const [testName, setTestName] = useState('');
  const [testFilename, setTestFilename] = useState('');
  const [dataFilePath, setDataFilePath] = useState('');
  const [result, setResult] = useState(null);
  const [reportUrl, setReportUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post('http://localhost:5000/run-layer', {
      layer,
      testName,
      testFilename,
      dataFilePath
    });
    setResult(res.data);
    if (res.data.reportUrl) setReportUrl(res.data.reportUrl);
  };

  return (
    <>
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #e0e7ff 0%, #f8fafc 100%)',
        display: 'flex',
        flexDirection: 'row',
        fontFamily: 'Inter, sans-serif'
      }}>
        {/* Left Panel: Test Input */}
        <div style={{
          minWidth: 340,
          maxWidth: 420,
          background: '#fff',
          borderRadius: 16,
          boxShadow: '0 4px 24px rgba(0,0,0,0.08)',
          padding: 32,
          margin: 40,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
          height: 'fit-content'
        }}>
          <h2 style={{ color: '#3b82f6', marginBottom: 24 }}>RFDC Automation Runner</h2>
          <form onSubmit={handleSubmit}>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 22 }}>
              <div>
                <label style={{ fontWeight: 600, color: '#3b82f6', fontSize: 15 }}>Layer:</label>
                <select
                  value={layer}
                  onChange={e => setLayer(e.target.value)}
                  style={{ marginLeft: 12, padding: 8, borderRadius: 8, border: '1px solid #cbd5e1', width: 220, fontSize: 15 }}
                >
                  {layers.map(l => (
                    <option key={l.value} value={l.value}>{l.label}</option>
                  ))}
                </select>
              </div>
              <div>
                <label style={{ fontWeight: 600, color: '#3b82f6', fontSize: 15 }}>Test Name:</label>
                <input
                  value={testName}
                  onChange={e => setTestName(e.target.value)}
                  placeholder={'e.g. MQTT Publish Test'}
                  style={{ marginLeft: 12, padding: 8, borderRadius: 8, border: '1px solid #cbd5e1', width: 220, fontSize: 15 }}
                />
              </div>
              <div>
                <label style={{ fontWeight: 600, color: '#3b82f6', fontSize: 15 }}>Test Filename:</label>
                <input
                  value={testFilename}
                  onChange={e => setTestFilename(e.target.value)}
                  placeholder={'e.g. /tests/mqtt_test.robot'}
                  style={{ marginLeft: 12, padding: 8, borderRadius: 8, border: '1px solid #cbd5e1', width: 220, fontSize: 15 }}
                />
              </div>
              <div>
                <label style={{ fontWeight: 600, color: '#3b82f6', fontSize: 15 }}>Test Data File Path:</label>
                <input
                  value={dataFilePath}
                  onChange={e => setDataFilePath(e.target.value)}
                  placeholder={'e.g. /tests/data/mqtt.json'}
                  style={{ marginLeft: 12, padding: 8, borderRadius: 8, border: '1px solid #cbd5e1', width: 220, fontSize: 15 }}
                />
              </div>
              <button
                type="submit"
                style={{
                  background: 'linear-gradient(90deg, #3b82f6 0%, #6366f1 100%)',
                  color: '#fff',
                  border: 'none',
                  borderRadius: 12,
                  padding: '12px 32px',
                  fontWeight: 700,
                  fontSize: 18,
                  cursor: 'pointer',
                  boxShadow: '0 2px 12px rgba(59,130,246,0.12)',
                  marginTop: 18
                }}
              >Run Automation</button>
            </div>
          </form>
        </div>
        {/* Right Panel: Progress & Reports */}
        <div style={{
          flex: 1,
          margin: '40px 0',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'flex-start',
          justifyContent: 'flex-start',
          minWidth: 400
        }}>
          {result && (
            <div style={{ marginTop: 28 }}>
              <h4 style={{ color: '#64748b' }}>Execution Progress</h4>
              <pre style={{ background: '#f1f5f9', padding: 16, borderRadius: 8 }}>{JSON.stringify(result, null, 2)}</pre>
            </div>
          )}
          <ReportViewer reportUrl={reportUrl} />
          <footer style={{ marginTop: 40, color: '#94a3b8', fontSize: 14 }}>
            Powered by Robot Framework, BrowserStack, Allure
          </footer>
        </div>
      </div>
    </>
  );
}

export default App;
